using Xunit;
using System.Text.Json;
using System.Security.Cryptography;

namespace DecryptTool.Tests
{
    public class DecryptToolTests
    {
        [Fact()]
        public void DecryptTest()
        {
            var json = JsonSerializer.Deserialize<JsonElement>("""{"encryptedAnswers":"1C6oeBKm6/hjIRT+E2BYrQD+2+oD7op7iiCmyijPVg==","encryptedKey":"","iv":"rmLOHTCP4CnqqA26","signingKey":"-----BEGIN PUBLIC KEY-----\nMIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEA1O2IB+zQH01J6MW+ZpyH/lENnr1ny9et\nGHi7H2xvo/l4yeJXIQg0H8rJfp59wxtEL0YnOzB9SFByNEwoDsd7D03PvLOhth6605Yp9Tk2mTxf\n9YFdXD+voWhjInvl+2X+Ny8OUctdOS1P/3GOBn4+AHBd6QCyMxRUljOx7khzTkWckPafk6Ft9k1W\nzkVso0ID+Yr553g4VOn8UBIYP/61x5GP/WlWvUnKnQw5x+gXEYEBW0uJ5zNqkh/AB851SWsWCoPz\nD2PiHKGrUygRVSzjZa1fJhP+fa/29SxWnH6IiEmrVXHyTYkZ4gVYTLX31cKv6yM9w4BcgCe2Gy65\nvyP63QIDAQAB\n-----END PUBLIC KEY-----"}""");
            var privateKeyBytes = Convert.FromBase64String("");

            var privateKey = RSA.Create();
            privateKey.ImportPkcs8PrivateKey(privateKeyBytes, out _);

            var decrypted = Program.Decrypt(json, privateKey);

            Assert.Equal("""{"test":"test"}""", decrypted);
        }
    }
}